import React from "react";
import WeatherIcon from "./WeatherIcon";

export default function FormatHourlyForecast(props) {
  function time() {
let date = new Date(props.data.dt * 1000);
let hours = date.getHours();
    if (hours < 10) {
        hours = `0${hours}`;
    };
    let minutes = date.getMinutes();
    if (minutes < 10) {
        minutes = `0${minutes}`;
    };
    return <div>{hours}:{minutes}</div>;

  } 
  
  return (
        <div className="forecast">
          <WeatherIcon icon={props.data.weather[0].icon} size={40}/>
          <p>
            {time()} 
            <br />
            <span >{Math.round(props.data.temp)}ยบ </span> 
          <br /> 
          <span className="forecast-description text-capitalize">{props.data.weather[0].description}</span>
          </p>
        </div>
    )
}
