import React, { useState } from "react";
import "./Forecast.css";
import WeatherIcon from "./WeatherIcon";
import axios from "axios";
import FormatForecast from "./FormatForecast";

export default function Forecast(props) {
  const apiKey = "91afe9171359147d9b8594d8a2e6e434";
  let forecastApiUrl = `http://api.openweathermap.org/data/2.5/onecall?lat=${props.data.lat}&lon=${props.data.lon}&appid=${apiKey}&units=metric`;

  const [loaded, setLoaded] = useState(false);
  const [forecast, setForecast] = useState(null);

  function showForecast(response) {
    setLoaded(true);
    setForecast(response.data.daily);
  }

if (loaded) {
  return (
    <div className="card">
      <div className="card-body">
    <div className="row Forecast" id="forecast">
      <div className="col-2 text-center">
        <FormatForecast data={forecast}/>
      </div>
    </div>
    </div>
    </div>
  );

}

  axios.get(forecastApiUrl).then(showForecast);


}
